package com.scavi.brainsqueeze.adventofcode2016;

import org.junit.Assert;
import org.junit.Test;

import java.util.List;

import static com.scavi.brainsqueeze.adventofcode2016.Day2BathroomSecurity.Instruction;

/**
 * @author Michael Heymel
 * @since 02/12/16
 */
public class Day2BathroomSecurityTest {

    @Test
    public void testPreparationGrid1() {
        String[] input = new String[]{"ULL", "RRDDD", "LURDL", "UUUUD"};
        Day2BathroomSecurity securityTest = new Day2BathroomSecurity();
        List<List<Instruction>> instructions = Instruction.from(input);
        String resultCode = securityTest.determineGridCode(instructions);
        Assert.assertEquals("1985", resultCode);
    }


    @Test
    public void testPreparationCross1() {
        String[] input = new String[]{"ULL", "RRDDD", "LURDL", "UUUUD"};
        Day2BathroomSecurity securityTest = new Day2BathroomSecurity();
        List<List<Instruction>> instructions = Instruction.from(input);
        String resultCode = securityTest.determineCrossCode(instructions);
        Assert.assertEquals("5DB3", resultCode);
    }


    @Test
    public void testGridQuestion() {
        String[] input = new String[]{
                "",
                "",
                "",
                "",
                ""};
        Day2BathroomSecurity securityTest = new Day2BathroomSecurity();
        List<List<Instruction>> instructions = Instruction.from(input);
        String resultCode = securityTest.determineGridCode(instructions);
        Assert.assertEquals("92435", resultCode);
    }

    @Test
    public void testCrossQuestion() {
        String[] input = new String[]{
                "",
                "",
                "",
                "",
                ""};
        Day2BathroomSecurity securityTest = new Day2BathroomSecurity();
        List<List<Instruction>> instructions = Instruction.from(input);
        String resultCode = securityTest.determineCrossCode(instructions);
        Assert.assertEquals("C1A88", resultCode);
    }
}
